    public static int FibonacciIterative(int n)
    {
        if (n <= 0)
            throw new ArgumentException("The input value must be greater than zero.", nameof(n));

        if (n == 1 || n == 2)
            return 1;

        int prev = 1;
        int current = 1;
        int result = 0;

        for (int i = 2; i < n; i++)
        {
            result = prev + current;
            prev = current;
            current = result;
        }

        return result;
    }

    public static int FibonacciRecursive(int n)
    {
        if (n <= 0)
            throw new ArgumentException("The input value must be greater than zero.", nameof(n));

        if (n == 1 || n == 2)
            return 1;

        return FibonacciRecursive(n - 1) + FibonacciRecursive(n - 2);
    }
}

